import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { TarjetaService } from '../../services/tarjeta.service';
@Component({
  selector: 'app-tarjeta-credito',
  templateUrl: './tarjeta-credito.component.html',
  styleUrls: ['./tarjeta-credito.component.css']
})
export class TarjetaCreditoComponent {
  listTarjeta: any[] = [

  ];
  accion = "agregar";
  form: FormGroup;
  id: number | undefined;

  constructor(private fb: FormBuilder, private toastr: ToastrService,
    private _tarjetaService: TarjetaService
  ) {
    this.form = this.fb.group({
      titulo: ['', Validators.required],
      numeroTarjeta: ['', [Validators.required, Validators.maxLength(16), Validators.minLength(16)]],
      fechaExpiracion: ['', [Validators.required]],
      cvv: ['', [Validators.required, Validators.maxLength(3), Validators.minLength(3)]],


    })


  }
  ngOnInit(): void {
    this.obtenerTarjetas();
  }

  obtenerTarjetas() {

    this._tarjetaService.getListTarjetas().subscribe(data => {
      this.listTarjeta = data.meals;
      console.log(data.meals);
    }, error => {
      console.log(error);

    })
  }


  agregarTarjeta() {
    const tarjeta: any = {
      titulo: this.form.get('titulo')?.value,
      numeroTarjeta: this.form.get('numeroTarjeta')?.value,
      fechaExpiracion: this.form.get('fechaExpiracion')?.value,
      cvv: this.form.get('cvv')?.value,

    }
    if (this.id == undefined) {
      /* AGREGAR */
      this._tarjetaService.saveTarjeta(tarjeta).subscribe(data => {
        this.toastr.success('Tarjeta Registrada', 'Tarjeta registrada');
        this.form.reset();
        this.obtenerTarjetas();
      }, error => {
        console.log(error);
      })

    } else {
      /* editar */
      tarjeta.id = this.id;
      this._tarjetaService.updateTarjeta(this.id, tarjeta).subscribe(data => {
        this.form.reset();
        this.accion = 'Agregar';
        this.id = undefined;
        this.toastr.info('Tarjeta actualizada', 'Tarjeta actualizada');
        this.obtenerTarjetas();
      }, error => {
        console.log(error);
      })
    }


  }

  eliminarTarjeta(id: number) {
    this._tarjetaService.deleteTarjeta(id).subscribe(data => {
      this.toastr.error('Tarjeta Eliminada con exito', 'Tarjeta Eliminada');
      this.obtenerTarjetas();
    }, error => {
      console.log(error);
    })

  }

  editarTarjeta(tarjeta: any) {
    this.accion = 'EDITAR';
    this.id = tarjeta.id;
    this.form.patchValue({
      titulo: tarjeta.titulo,
      numeroTarjeta: tarjeta.numeroTarjeta,
      fechaExpiracion: tarjeta.fechaExpiracion,
      cvv: tarjeta.cvv
    })



  }

}
<!DOCTYPE html>
<html lang="en">
  <head>
    <title>Title</title>
    <!-- Required meta tags -->
    <meta charset="utf-8" />
    <meta
      name="viewport"
      content="width=device-width, initial-scale=1, shrink-to-fit=no"
    />

    <!-- Bootstrap CSS -->
  </head>
  <body>
    <div class="container mt-5">
      <div class="row">
        <div class="col-lg-8 offset-lg-2 text-center">
          <div class="card">
            <!--tips: add .text-center,.text-right to the .card to change card text alignment-->
            <div class="card-body">
              <h5 class="card-title">TARJETA DE CREDITO</h5>
            </div>
          </div>
        </div>
      </div>

    <div class="mt-4">
      <div class="row">


        <div class="col-lg-12">
          <div class="card">
            <div class="card-body text-center">
              <H4> LISTADO DE TARJETAS </H4>

              <table class="table table-light text-center">
                <tbody>
                  <tr *ngFor="let tarjeta of listTarjeta; let i = index">
                    <td>{{ tarjeta.strArea }}</td>

                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  </body>
</html>
