<div class="container mt-5">
  <div class="row">
    <div class="col-lg-8 offset-lg-2 text-center">
      <div class="card">
        <!--tips: add .text-center,.text-right to the .card to change card text alignment-->
        <div class="card-body">
          <h5 class="card-title">Platillos</h5>
        </div>
      </div>
    </div>
  </div>

<div class="mt-4">
  <div class="row">


    <div class="col-lg-12">
      <div class="card">
        <div class="card-body text-center">
          <H4> LISTADO DE Platillos </H4>

          <table class="table table-light text-center">
            <tbody>
              <tr *ngFor="let tarjeta of listTarjeta; let i = index">
               <!--  <td>  <button mat-stroked-button routerLink="/platillos/{{ tarjeta.strIngredient }}"  routerLinkActive="active">

                  {{ tarjeta.strMeal }}
                </button></td>
                <td>
                  {{ tarjeta.strMeal }}
                </td> -->
                <td>

                  <mat-tree >
                    <!-- This is the tree node template for leaf nodes -->
                    <mat-tree-node  matTreeNodePadding>
                      <!-- use a disabled button to provide padding for tree leaf -->
                      <button mat-icon-button disabled></button>
                    </mat-tree-node>
                    <!-- This is the tree node template for expandable nodes -->
                    <mat-tree-node  matTreeNodePadding>
                      <button mat-icon-button matTreeNodeToggle
                             >
                        <mat-icon class="mat-icon-rtl-mirror">
                          {{treeControl.isExpanded(node) ? 'expand_more' : 'chevron_right'}}
                        </mat-icon>
                      </button>
                      {{node.name}}
                    </mat-tree-node>
                  </mat-tree>

              </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</div>
</div>
