import { Component } from '@angular/core';
import { PlatillosService } from '../../services/platillos.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import {ActivatedRoute} from '@angular/router';
@Component({
  selector: 'app-platillos',
  templateUrl: './platillos.component.html',
  styleUrls: ['./platillos.component.css']
})
export class PlatillosComponent {
  listTarjeta: any[] = [

  ];
  accion = "agregar";
  form: FormGroup;
  id: number | undefined;

  constructor(private fb: FormBuilder, private toastr: ToastrService,
    private _ingredienteservice: PlatillosService,private _route: ActivatedRoute
  ) {
    this.form = this.fb.group({
      titulo: ['', Validators.required],
      numeroTarjeta: ['', [Validators.required, Validators.maxLength(16), Validators.minLength(16)]],
      fechaExpiracion: ['', [Validators.required]],
      cvv: ['', [Validators.required, Validators.maxLength(3), Validators.minLength(3)]],


    })

  }
  ngOnInit(): void {
    let id = this._route.snapshot.paramMap.get('id');
    console.log(id);

    this.obtenerTarjetas(id);
  }

  obtenerTarjetas(id: any) {

    this._ingredienteservice.getListTarjetas(id).subscribe(data => {
      this.listTarjeta = data.meals;
      console.log(data.meals);
    }, error => {
      console.log(error);

    })
  }




  editarTarjeta(tarjeta: any) {
    this.accion = 'EDITAR';
    this.id = tarjeta.id;
    this.form.patchValue({
      titulo: tarjeta.titulo,
      numeroTarjeta: tarjeta.numeroTarjeta,
      fechaExpiracion: tarjeta.fechaExpiracion,
      cvv: tarjeta.cvv
    })



  }
}

<app-menu></app-menu>
<div class="container mt-5">
  <div class="row">
    <div class="col-lg-8 offset-lg-2 text-center">
      <div class="card">
        <!--tips: add .text-center,.text-right to the .card to change card text alignment-->
        <div class="card-body">
          <h5 class="card-title">Platillos</h5>
        </div>
      </div>
    </div>
  </div>

<div class="mt-4">
  <div class="row">


    <div class="col-lg-12">
      <div class="card">
        <div class="card-body text-center">
          <H4> LISTADO DE Platillos </H4>

          <table class="table table-light text-center">
            <tbody>
              <tr *ngFor="let tarjeta of listTarjeta; let i = index">
                <td>  <button mat-stroked-button routerLink="/platillos/{{ tarjeta.strIngredient }}"  routerLinkActive="active">

                  {{ tarjeta.strMeal }}
                </button></td>
                <td>
                  {{ tarjeta.strMeal }}
                </td>
                <td>

                  <mat-card class="example-card">
                    <mat-card-header>
                      <div mat-card-avatar class="example-header-image"></div>
                      <mat-card-title>Shiba Inu</mat-card-title>
                      <mat-card-subtitle>Dog Breed</mat-card-subtitle>
                    </mat-card-header>
                    <img mat-card-image src="https://material.angular.io/assets/img/examples/shiba2.jpg" alt="Photo of a Shiba Inu">
                    <mat-card-content>
                      <p>
                        The Shiba Inu is the smallest of the six original and distinct spitz breeds of dog from Japan.
                        A small, agile dog that copes very well with mountainous terrain, the Shiba Inu was originally
                        bred for hunting.
                      </p>
                    </mat-card-content>
                    <mat-card-actions>
                      <button mat-button>LIKE</button>
                      <button mat-button>SHARE</button>
                    </mat-card-actions>
                  </mat-card>

              </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</div>
</div>
