import { Component } from '@angular/core';
import { PlatillosService } from '../../services/platillos.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import {ActivatedRoute} from '@angular/router';
import {MatDialog, MAT_DIALOG_DATA} from '@angular/material/dialog';
export interface DialogData {
  animal: 'panda' | 'unicorn' | 'lion';
}
@Component({
  selector: 'app-platillos',
  templateUrl: './platillos.component.html',
  styleUrls: ['./platillos.component.css']
})
export class PlatillosComponent {
  listTarjeta: any[] = [

  ];
  accion = "agregar";
  form: FormGroup;
  id: number | undefined;

  constructor(private fb: FormBuilder, private toastr: ToastrService,
    private _ingredienteservice: PlatillosService,private _route: ActivatedRoute,public dialog: MatDialog
  ) {
    this.form = this.fb.group({
      titulo: ['', Validators.required],
      numeroTarjeta: ['', [Validators.required, Validators.maxLength(16), Validators.minLength(16)]],
      fechaExpiracion: ['', [Validators.required]],
      cvv: ['', [Validators.required, Validators.maxLength(3), Validators.minLength(3)]],


    })

  }

  ngOnInit(): void {
    let id = this._route.snapshot.paramMap.get('id');
    console.log(id);

    this.obtenerTarjetas(id);
  }

  obtenerTarjetas(id: any) {

    this._ingredienteservice.getListTarjetas(id).subscribe(data => {
      this.listTarjeta = data.meals;
      console.log(data.meals);
    }, error => {
      console.log(error);

    })
  }




  editarTarjeta(tarjeta: any) {
    this.accion = 'EDITAR';
    this.id = tarjeta.id;
    this.form.patchValue({
      titulo: tarjeta.titulo,
      numeroTarjeta: tarjeta.numeroTarjeta,
      fechaExpiracion: tarjeta.fechaExpiracion,
      cvv: tarjeta.cvv
    })



  }
}

<app-menu></app-menu>
<div class="container mt-5">
  <div class="row">
    <div class="col-lg-8 offset-lg-2 text-center">
      <div class="card">
        <!--tips: add .text-center,.text-right to the .card to change card text alignment-->
        <div class="card-body">
          <h5 class="card-title">Platillos</h5>
        </div>
      </div>
    </div>
  </div>

<div class="mt-4">
  <div class="row">


    <div class="col-lg-12">
      <div class="card">
        <div class="card-body text-center">
          <H4> LISTADO DE Platillos </H4>

          <table class="table table-light text-center">
            <tbody>
              <tr *ngFor="let tarjeta of listTarjeta; let i = index">
                <td>  <button  mat-button (click)="openDialog()"  routerLinkActive="active">


                </button></td>
                <td>
                  <mat-card class="example-card">
                    <mat-card-header>
                      <mat-card-title-group>
                        <mat-card-title>Shiba Inu</mat-card-title>
                        <mat-card-subtitle>Small</mat-card-subtitle>
                        <img mat-card-sm-image src="https://material.angular.io/assets/img/examples/shiba2.jpg" >
                      </mat-card-title-group>
                    </mat-card-header>
                    <mat-card-content>
                      {{ tarjeta.strMeal }}
                    </mat-card-content>
                  </mat-card>
                </td>

              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</div>
</div>

<h1 mat-dialog-title>Favorite Animal</h1>
<div mat-dialog-content>
  My favorite animal is:
  <ul>
    <li>
      <span *ngFor="let tarjeta of listTarjeta; let i = index">&#10003;</span> Panda
    </li>
   <!--  <li>
      <span *ngIf="data.animal === 'unicorn'">&#10003;</span> Unicorn
    </li>
    <li>
      <span *ngIf="data.animal === 'lion'">&#10003;</span> Lion
    </li> -->
  </ul>
</div>
