import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { TarjetaService } from '../../services/tarjeta.service';
@Component({
  selector: 'app-tarjeta-credito',
  templateUrl: './tarjeta-credito.component.html',
  styleUrls: ['./tarjeta-credito.component.css']
})
export class TarjetaCreditoComponent {
  listTarjeta: any[] = [

  ];
  accion = "agregar";
  form: FormGroup;
  id: number | undefined;

  constructor(private fb: FormBuilder, private toastr: ToastrService,
    private _tarjetaService: TarjetaService
  ) {
    this.form = this.fb.group({
      titulo: ['', Validators.required],
      numeroTarjeta: ['', [Validators.required, Validators.maxLength(16), Validators.minLength(16)]],
      fechaExpiracion: ['', [Validators.required]],
      cvv: ['', [Validators.required, Validators.maxLength(3), Validators.minLength(3)]],


    })


  }
  ngOnInit(): void {
    this.obtenerTarjetas();
  }

  obtenerTarjetas() {

    this._tarjetaService.getListTarjetas().subscribe(data => {
      this.listTarjeta = data.meals;
      console.log(data.meals);
    }, error => {
      console.log(error);

    })
  }


  agregarTarjeta() {
    const tarjeta: any = {
      titulo: this.form.get('titulo')?.value,
      numeroTarjeta: this.form.get('numeroTarjeta')?.value,
      fechaExpiracion: this.form.get('fechaExpiracion')?.value,
      cvv: this.form.get('cvv')?.value,

    }
    if (this.id == undefined) {
      /* AGREGAR */
      this._tarjetaService.saveTarjeta(tarjeta).subscribe(data => {
        this.toastr.success('Tarjeta Registrada', 'Tarjeta registrada');
        this.form.reset();
        this.obtenerTarjetas();
      }, error => {
        console.log(error);
      })

    } else {
      /* editar */
      tarjeta.id = this.id;
      this._tarjetaService.updateTarjeta(this.id, tarjeta).subscribe(data => {
        this.form.reset();
        this.accion = 'Agregar';
        this.id = undefined;
        this.toastr.info('Tarjeta actualizada', 'Tarjeta actualizada');
        this.obtenerTarjetas();
      }, error => {
        console.log(error);
      })
    }


  }

  eliminarTarjeta(id: number) {
    this._tarjetaService.deleteTarjeta(id).subscribe(data => {
      this.toastr.error('Tarjeta Eliminada con exito', 'Tarjeta Eliminada');
      this.obtenerTarjetas();
    }, error => {
      console.log(error);
    })

  }

  editarTarjeta(tarjeta: any) {
    this.accion = 'EDITAR';
    this.id = tarjeta.id;
    this.form.patchValue({
      titulo: tarjeta.titulo,
      numeroTarjeta: tarjeta.numeroTarjeta,
      fechaExpiracion: tarjeta.fechaExpiracion,
      cvv: tarjeta.cvv
    })



  }

}
<!DOCTYPE html>
<html lang="en">
  <head>
    <title>Title</title>
    <!-- Required meta tags -->
    <meta charset="utf-8" />
    <meta
      name="viewport"
      content="width=device-width, initial-scale=1, shrink-to-fit=no"
    />

    <!-- Bootstrap CSS -->
  </head>
  <body>
    <div class="container mt-5">
      <div class="row">
        <div class="col-lg-8 offset-lg-2 text-center">
          <div class="card">
            <!--tips: add .text-center,.text-right to the .card to change card text alignment-->
            <div class="card-body">
              <h5 class="card-title">TARJETA DE CREDITO</h5>
            </div>
          </div>
        </div>
      </div>

    <div class="mt-4">
      <div class="row">
        <div class="col-lg-6">
          <div class="card">
            <div class="card-body">
              <H4> {{ accion | uppercase}} TARJETA </H4>
              <form [formGroup]="form" (ngSubmit)="agregarTarjeta()">
                <div class="input-group mb-3">
                  <label class="sr-only" for=""
                    >Nombre</label
                  >
                  <div class="input-group mb-2">
                    <div class="input-group-prepend">
                      <div class="input-group-text"><i class="fa-solid fa-user" [class.green-icon]="this.form.get('titulo')?.valid"
                        [class.red-icon]="this.form.get('titulo')?.invalid && this.form.get('titulo')?.touched"
                        ></i></div>
                    </div>
                    <input
                    formControlName="titulo"
                      type="text"
                      class="form-control"
                      id=""
                      placeholder="Nombre"
                    />
                  </div>
                </div>

                <div class="input-group mb-3">
                  <label class="sr-only" for=""
                    >NUMERO DE TARJETA</label
                  >
                  <div class="input-group mb-2">
                    <div class="input-group-prepend">
                      <div class="input-group-text"><i class="fa-regular fa-credit-card"
                        [class.green-icon]="this.form.get('numeroTarjeta')?.valid"
                        [class.red-icon]="this.form.get('numeroTarjeta')?.invalid && this.form.get('numeroTarjeta')?.touched"
                        ></i></div>
                    </div>
                    <input
                    formControlName="numeroTarjeta"
                      type="text"
                      class="form-control"
                      id=""
                      placeholder="NUMERO DE TARJETA"
                      maxlength="16"

                    />
                  </div>
                </div>

                <div class="row">
                  <div class="col-lg-7">
                    <div class="input-group mb-3">
                      <label class="sr-only" for=""
                        >FECHA DE EXPITRACION</label
                      >
                      <div class="input-group mb-2">
                        <div class="input-group-prepend">
                          <div class="input-group-text"><i class="fa-solid fa-calendar-days"
                            [class.green-icon]="this.form.get('fechaExpiracion')?.valid"
                            [class.red-icon]="this.form.get('fechaExpiracion')?.invalid && this.form.get('fechaExpiracion')?.touched"
                            ></i></div>
                        </div>
                        <input
                        formControlName="fechaExpiracion"
                          type="text"
                          class="form-control"
                          id=""
                          placeholder="MM/YY"
                        />
                      </div>
                    </div>
                  </div>

                  <div class="col-lg-5">
                    <div class="input-group mb-3">
                      <label class="sr-only" for=""
                        >CVV</label
                      >
                      <div class="input-group mb-2">
                        <div class="input-group-prepend">
                          <div class="input-group-text"><i class="fa-solid fa-key"
                            [class.green-icon]="this.form.get('cvv')?.valid"
                            [class.red-icon]="this.form.get('cvv')?.invalid && this.form.get('cvv')?.touched"
                            ></i></div>
                        </div>
                        <input
                        formControlName="cvv"
                          type="password"
                          class="form-control"
                          id=""
                          placeholder="CVV"
                          maxlength="3"
                        />
                      </div>
                    </div>
                  </div>
                </div>
                  <div class="d-grid gap-2">
                    <button [disabled]="this.form.invalid" type="submit" class="btn btn-success btn-lg btn-block">{{ accion | uppercase}}</button>

                  </div>
              </form>
            </div>
          </div>
        </div>

        <div class="col-lg-6">
          <div class="card">
            <div class="card-body">
              <H4> LISTADO DE TARJETAS </H4>

              <table class="table table-light">
                <tbody>
                  <tr *ngFor="let tarjeta of listTarjeta; let i = index">
                    <td>{{ tarjeta.strArea }}</td>


                    <td><i  (click)="editarTarjeta(tarjeta)"  class="fa-solid fa-pen-to-square"></i></td>
                    <td><i  (click)="eliminarTarjeta(tarjeta.id)" class="fa-solid fa-trash"></i></td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  </body>
</html>
